    public static int maxSubArrayLength(List<Integer> badges) {
        int max = 0;
        for (int i = 0; i < badges.size(); i++) {
            Integer prev = null;
            for (int j = i + 1; j < badges.size(); j++) {
                int product = badges.get(i) * badges.get(j);
                if (prev == null) {
                    product = badges.get(i) * badges.get(j);
                    prev = product;
                } else {
                    prev *= badges.get(j);
                    product = prev;
                }
                if (product == 1) {
                    max = Math.max(j - i, max);
                }
            }
//            size=2;
        }
        return max + 1;

    }

    public static int maxSubArrayLengthOne(List<Integer> badges) {
        int[][] lookup = new int[badges.size()][badges.size()];
        int result = 0;
        for (int i = badges.size() - 1; i >= 0; i--) {
            for (int j = i; j < badges.size(); j++) {
                if (i == j) {
                    lookup[i][j] = badges.get(i);
                } else if (i == 0) {
                    lookup[i][j] = lookup[i][j - 1] * badges.get(j);
                } else {
                    lookup[i][j] = lookup[i][j - 1] * lookup[i + 1][j];
                }
                if (lookup[i][j] == 1) {
                    int diff = (j - i) + 1;
                    result = Math.max(diff, result);
                }
            }

        }
        return result;
    }
